namespace EcommerceApi.Services
{
    public class ProductService: IProductService
    {
        EcommerceContext dbContext;
        private readonly IWebHostEnvironment _webHostEnvironment;
        public ProductService(EcommerceContext _db, IWebHostEnvironment webHostEnvironment)
        {
            dbContext = _db;
            _webHostEnvironment = webHostEnvironment;
        }
        public Product AddProduct(Product request)
        {
            string FileExtension = "JPEG";
            string FolderName = "ProductImages";
            string path = "";

            var FileName = $"{Guid.NewGuid().ToString()}.{FileExtension}";
            string contentRootPath = _webHostEnvironment.ContentRootPath;
            path = Path.Combine(contentRootPath, FolderName, FileName);
            //or path = Path.Combine(contentRootPath , "wwwroot" ,"CSS" );

            using (FileStream fileStream = new FileStream(
                path,
                FileMode.OpenOrCreate,
                FileAccess.ReadWrite,
                FileShare.ReadWrite
            ))
            {
                string fileBytes = "";
                fileStream.Write(Convert.FromBase64String(fileBytes));
                fileStream.Close();

            }
            var checkProduct = dbContext.Products
                .Select(x => x.ProductName == request.ProductName)
                .FirstOrDefault();
            if(checkProduct)
            {
                throw new Exception();
            }
            var newProduct = new Product
            {
                ProductName = request.ProductName,
                Price = request.Price,
                ArchivedInd = false,
                Description = request.Description
            };
            using (var target = new MemoryStream())
            {
                request.CoverImage = target.ToArray();
            }
            dbContext.Products.Add(request);
            dbContext.SaveChanges();
            return request;
        }
        public PageResult<Product> GetAllProducts(ODataQueryOptions<Product> oDataQueryOptions)
        {
            var result = dbContext.Products.Select(x=> x);
            return GetPageResult(result, oDataQueryOptions);
        }

        public static PageResult<T> GetPageResult<T>(
            IQueryable<T> input,
            ODataQueryOptions<T> options
            )
        {
            if (options == null || input == null)
            {
                return new PageResult<T>(null, null, 0);
            }

            return GetPageResult(input, options, new ODataQuerySettings());
        }

        private static PageResult<T> GetPageResult<T>(IQueryable<T> input, ODataQueryOptions options, ODataQuerySettings querySettings)
        {
            IEnumerable<T> results = (IQueryable<T>)options.ApplyTo(input, querySettings);

            Uri nextLink = null;

            var skipValue = options.Skip?.Value ?? 0;
            var topValue = options.Top?.Value;
            var count = options.Count?.GetEntityCount(options.Filter?.ApplyTo(input, new ODataQuerySettings()) ?? input);
            var nextSkip = NextSkip(skipValue, topValue, count);

            //if (nextSkip.HasValue)
            //{
            //    nextLink = new Uri(options.Request.RequestUri.AbsoluteUri)
            //        .WithQueryParam(SkipParamKey, nextSkip.Value.ToString());
            //}

            var returnVal = new PageResult<T>(
                results.ToList(),
                nextLink,
                count);

            return returnVal;
        }

        private const string SkipParamKey = "$skip";
        private static int? NextSkip(int? skipValue, int? topValue, long? totalCount)
        {
            if (!skipValue.HasValue || !topValue.HasValue || !totalCount.HasValue)
            {
                return null;
            }

            var nextPage = skipValue / topValue + 1;
            if (topValue * nextPage < totalCount)
            {
                return skipValue + topValue;
            }

            return null;
        }
    }
}
